mapper.select:
select * from ( select c.id,c.name,c.parent_id,c.icon_name,c.url, c.desc from account f left join account_role_ref e on f.id=e.role_id left join authority_role_ref b on b.role_id=e.role_id left join authority a on a.id=b.authority_id left join menu c on c.id=a.module_id left join permission d on d.id=a.permission_id where f.id=#{accountId}
and c.parent_id= #{parentId}
and c.parent_id is null
UNION select c.id,c.name,c.parent_id,c.icon_name,c.url, c.desc from account f left join user e on f.user_id=e.id left join post_role_ref g on e.post_id=g.post_id left join authority_role_ref b on b.role_id=g.role_id left join authority a on a.id=b.authority_id left join menu c on c.id=a.module_id left join permission d on d.id=a.permission_id where f.id=#{accountId}
and c.parent_id= #{parentId}
and c.parent_id is null
) a
